// {{{ Generators
template <typename Function>
vector<vector2> Generator(Function fx, Function fy, float limit, float scale, float step)
{
	vector<vector2> ret;
	for (float t = 0.0; t < limit; t += step) {
		ret.push_back(vector2(scale*fx(t), scale*fy(t)));
	}
	return ret;
}

vector<vector2> GenerateBox(float lux, float luy, float rdx, float rdy)
{
	vector<vector2> ret;
	ret.push_back(vector2(lux, luy));
	ret.push_back(vector2(lux, rdy));
	ret.push_back(vector2(rdx, rdy));
	ret.push_back(vector2(rdx, luy));
	return ret;
}
//}}}

Object magic_stick(Generator(stickX, stickY, 52*PI, 1.15, 0.01), GL_POINTS);
unsigned last_launched;
bool is_stick_launched;
vector<Object> obstacles;
unsigned score = 0;

void InitItems(void)
{
	score = 0;
	magic_stick.move(0., 0.);
	magic_stick.rotate(0.);
	obstacles.clear();
	auto obspika = Object(Generator(pikaX, pikaY, 52*PI, 0.15, 0.01), GL_POINTS);
	obspika.move(WindowSize::right - 80, WindowSize::top - 80);
	obstacles.push_back(obspika);
	auto obsmaja = Object(Generator(majaX, majaY, 64*PI, 0.04, 0.01), GL_POINTS);
	obsmaja.move(WindowSize::right - 80, WindowSize::bot + 130);
	obstacles.push_back(obsmaja);
	auto obspurin = Object(Generator(purinX, purinY, 44*PI, 0.07, 0.01), GL_POINTS);
	obspurin.move(WindowSize::left + 60., WindowSize::bot + 130);
	obstacles.push_back(obspurin);
	auto obspai = Object(Generator(paiX, paiY, 60*PI, 0.14, 0.01), GL_POINTS);
	obspai.move(WindowSize::left + 130, WindowSize::top - 170);
	obstacles.push_back(obspai);
	auto obsggobu = Object(Generator(ggobuX, ggobuY, 76*PI, 0.05, 0.03), GL_POINTS);
	obsggobu.move(WindowSize::left + 100, WindowSize::bot + 60);
	obstacles.push_back(obsggobu);
	auto obsgyara = Object(Generator(gyaraX, gyaraY, 156*PI, 0.45, 0.03), GL_POINTS);
	obsgyara.move(WindowSize::left + 200., WindowSize::bot + 380);
	obstacles.push_back(obsgyara);
	auto obspichu = Object(Generator(pichuX, pichuY, 68*PI, 0.10, 0.01), GL_POINTS);
	obspichu.move(WindowSize::left + 600., WindowSize::bot + 380);
	obstacles.push_back(obspichu);
	auto obsmaril = Object(Generator(marilX, marilY, 80*PI, 0.35, 0.02), GL_POINTS);
	obsmaril.move(WindowSize::left + 400., WindowSize::bot + 80);
	obstacles.push_back(obsmaril);
	auto obswall1 = Object(GenerateBox(WindowSize::left-20., WindowSize::bot-10,
						WindowSize::right+10., WindowSize::bot-20), GL_POLYGON);
	auto obswall2 = Object(GenerateBox(WindowSize::left-20, WindowSize::top+10,
						WindowSize::right+10, WindowSize::top+20), GL_POLYGON);
	auto obswall3 = Object(GenerateBox(WindowSize::left-20., WindowSize::top+20,
						WindowSize::left-10, WindowSize::bot-20), GL_POLYGON);
	auto obswall4 = Object(GenerateBox(WindowSize::right+10, WindowSize::top+20,
						WindowSize::right+20., WindowSize::bot-20), GL_POLYGON);
	obswall1.stick(0, 3);
	obswall2.stick(0, -3);
	obswall3.stick(3, 0);
	obswall4.stick(-3, 0);
	obstacles.push_back(obswall1);
	obstacles.push_back(obswall2);
	obstacles.push_back(obswall3);
	obstacles.push_back(obswall4);
}

Object *bomb = NULL;
void display(void) {
	glClearColor(0., 0., 0., 1.0);
	glClear(GL_COLOR_BUFFER_BIT);
	glColor3f(1.0, 1.0, 1.0);
	glLoadIdentity();
	for (auto& p: obstacles)
		p.draw();
	glColor3f(1., 0., 0.);
	if (bomb) {
		glPolygonMode(GL_FRONT_AND_BACK, GL_LINE);
		bomb->draw();
		bomb->move(10000, 10000);
		glPolygonMode(GL_FRONT_AND_BACK, GL_FILL);
	}

	if (is_stick_launched) {
		glColor3f(0.34, 0.667, 0.864);
	} else {
		glColor3f(0.5, 0.5, 0.5);
	}
	
	magic_stick.draw();
	char score_buffer[128] = "";
	sprintf(score_buffer, "Score: %-8u", score);
	DrawText(score_buffer, 0., WindowSize::top-50);
	glFlush();
}

bool collision_lock;
void RefreshScene(int unused)
{
	if (!collision_lock) {
		collision_lock = true;

		if (is_stick_launched) {
			is_stick_launched = !magic_stick.update(obstacles);
		}
		
		float elapsed = 1<<20;
		bool stable = false;
		for (auto& o: obstacles) {
			elapsed = min(elapsed, clock() - float(o.last_effective_collision));
		}
		if (elapsed >= 3 * CLOCKS_PER_SEC)
			stable = true;

		if (stable) {
			for (auto& o: obstacles) {
					score += o.score * o.score;
					o.score = 0;
			}
		}

		for (auto& o: obstacles)
			o.update(obstacles);
		glutPostRedisplay();
		collision_lock = false;
	}
	glutTimerFunc(50 /*msecs*/, RefreshScene, 0 /*value to pass*/);
}

// {{{ Input
void keyboard(unsigned char key, int x, int y)
{
	switch (key) {
		case ' ':
			if (!is_stick_launched) {
				for (auto& o: obstacles) {
					score += o.score * o.score;
					o.score = 0;
				}
				float dx = sqrt(WindowSize::right) * cos(magic_stick.angle+PI/2),
					  dy = sqrt(WindowSize::top) * sin(magic_stick.angle+PI/2);
				magic_stick.set_velocity(dx, dy);
				last_launched = clock();
				is_stick_launched = true;
				glutPostRedisplay();
			}
			break;
	}
}

short rightbuttonpressed = 0;

void special(int key, int x, int y) {
	switch (key) {
	case GLUT_KEY_END:
		exit(0);
		break;
	case GLUT_KEY_LEFT:
		magic_stick.rotate(magic_stick.angle + 0.08);
		glutPostRedisplay();
		break;
	case GLUT_KEY_RIGHT:
		magic_stick.rotate(magic_stick.angle - 0.08);
		glutPostRedisplay();
		break;
	case GLUT_KEY_UP:
		{
			auto forward = magic_stick.center +
				vector2(7.*cos(magic_stick.angle+PI/2), 7.*sin(magic_stick.angle+PI/2));
			magic_stick.move(forward.x, forward.y);
			glutPostRedisplay();
		}
		break;
	case GLUT_KEY_DOWN:
		{
			auto backward = magic_stick.center -
				vector2(6.*cos(magic_stick.angle+PI/2), 6.*sin(magic_stick.angle+PI/2));
			magic_stick.move(backward.x, backward.y);
			glutPostRedisplay();
		}
		break;
	}
}

void mousepress(int button, int state, int x, int y) {
	if ((button == GLUT_LEFT_BUTTON) && (state == GLUT_DOWN)) {
		collision_lock = true;
		x -= WindowSize::right; y = -y + WindowSize::top;
		delete bomb;
		printf("*** The left mouse button was pressed at (%d, %d).\n", x, y);
		bomb = new Object(GenerateBox(-50, 50, 50, -50), GL_POLYGON);
		bomb->move(x, y);
		bomb->draw();
		bomb->set_velocity((rand()%2?1:-1)*(rand()%6), (rand()%2?1:-1)*(rand()%6));
		for (auto& o: obstacles)
			bomb->detect_collision(o);
		glutPostRedisplay();
		collision_lock = false;
	}
	else if ((button == GLUT_RIGHT_BUTTON) && (state == GLUT_DOWN))
		rightbuttonpressed = 1;
	else if ((button == GLUT_RIGHT_BUTTON) && (state == GLUT_UP))
		rightbuttonpressed = 0;
}

void mousemove(int x, int y) {
	if (rightbuttonpressed) {
		printf("$$$ The right mouse button is now at (%d, %d).\n", x, y);
	}
}
