// {{{ Reshape
float WindowSize::left = -330;
float WindowSize::right = 330;
float WindowSize::bot = -330;
float WindowSize::top = 330;

void reshape(int width, int height) {
	WindowSize::top = height / 2. - 20;
	WindowSize::bot = -height / 2. + 20;
	WindowSize::right = width / 2. - 20;
	WindowSize::left = -width / 2. + 20;
	glViewport(0, 0, width, height);
	glMatrixMode(GL_PROJECTION);
	glLoadIdentity();
	glOrtho(-width/2.0, width/2.0, -height/2.0, height/2.0, -1000., 1000);
	glMatrixMode(GL_MODELVIEW);
	glLoadIdentity();
	InitItems();
	glutPostRedisplay();
	printf("### The new window size is %dx%d.\n", width, height);
}
// }}}

// {{{ Boilerplate
void RegisterCallback(void) {
	glutDisplayFunc(display);
	glutKeyboardFunc(keyboard);
	glutMouseFunc(mousepress);
	glutTimerFunc(50 /*msecs*/, RefreshScene, 0 /*value to pass*/);
	glutMotionFunc(mousemove);
	glutSpecialFunc(special);
	glutReshapeFunc(reshape);
}



int main (int argc, char **argv) {
	glutInit (&argc, argv);
	glutInitDisplayMode (GLUT_RGBA);
	glutInitWindowSize (700, 700);
	glutCreateWindow ("Pokemon Billiard");
	RegisterCallback ();
	glutMainLoop ();
	return 0;

}
